INSERT INTO `article` VALUES ('241', '1', ' 关键词：高血压病理,高血压 ', '药物引起高血压机理摸清', null, '　　河南医大一附院肾病内科刘章锁教授等经过临床、药代动力学和药理研究，得出基因重组红细胞合成素所致高血压与内皮素升高，一氧化氮代谢物降低、肾组织一氧化氮合成酶表达减少有关。', '2008-10-17 12:00:12', null, null, null);
